css:
@import url('https://fonts.googleapis.com/css2?family=Roboto+Condensed&display=swap');

.body{
  font-family: 'Roboto Condensed', sans-serif !important;
}

#header{
  background: white !important;
  padding-bottom: 10px !important;
}
.header{
  position: relative !important;
}
.prices__installments{
   font-size: 20px !important;
}
.prices__installments span{
  font-weight: 600;
}
#menu-horizontal{
  padding: 15px !important;
  font-family: 'Roboto Condensed', sans-serif;
}
.header__nav a{
  border-radius: 0px !important;
  font-size: 18px !important;

}
.header__nav a:hover {
    background: #854f41;
    text-decoration: none;
    border-radius: 0px !important;
    color: white;
    padding: 7px;
}

@media (min-width: 768px){
.header__search-input {

  border-radius: 0px !important;
  border: 1px solid #854f41;

  }}

#user-button > span.d-none.d-md-inline , .header__buttons .btn{
text-transform: uppercase;
    color: #854f41 !important;
    font-weight: 600;
}
#content > div > section > div > div > span{
  text-transform: uppercase;
  font-weight: 700;
  font-size: 26px !important;
  color: #854f41;
  font-family: 'Roboto Condensed', sans-serif;
}

.products-carousel__title a, .products-carousel__title span{
  font-size: 20px;
  text-transform: uppercase;
  color: #854f41;
}


@media (min-width: 992px){
.product-card__pictures {
    height: 400px !important;
}}
@media (min-width: 992px){
.product-card__picture img {
    max-height: 400px !important;
}}


.product-card__name{
  text-align: center !important;
  color: #795f6e !important;
    font-style: italic !important;
    font-size: 14px !important;
}
.product-card{
  text-align: center !important;
}

.prices__value{
  display: none !important;
}
.prices__discount{
  font-size: 14px !important;
}
.product-card__buy.fade button {
  display:none;
}
.prices__discount span{
  font-weight: 600 !important;
  color: green !important;
}
@media (max-width: 500px){

  #content > div > div.container > nav > ol > li:nth-child(1) {
    display: block!important;
    font-size: 12px !important;
}

  #cart-count{
  background: transparent!important;
  top: 1.55rem !important;
  right: 1.15rem !important;
  color: #000 !important;    
  }
#cart-button > img{
  position: relative !important;
    bottom: 0px !important;
  }
.product-card__name {
    --max-lines: 3;
    /* padding-top: var(--spacer-1); */
    font-size: var(--font-size-sm);
    line-height: var(--line-height-sm);
    /* height: calc(var(--line-height-sm)*var(--font-size-sm)*var(--max-lines)); */
    box-sizing: content-box!important;
    overflow: hidden;
    font-weight: var(--font-normal);
    z-index: 1;
    display: -webkit-box;
    -webkit-line-clamp: var(--max-lines);
    -webkit-box-orient: vertical;
    /* height: 0px; */
  }
.prices__installments {
    font-size: 15px !important;
}

.prices__discount {
    font-size: 12px !important;
}  
.products-carousel__title a, .products-carousel__title span {
    font-size: 15px;
    text-transform: uppercase;
    color: #854f41;
}
  .product-card__pictures {
    height: 250px;
}
.product-card__picture img {
    max-height: 250px;
}
  #content > div > section > div > div > span {
    text-transform: uppercase;
    font-weight: 700;
    font-size: 15px !important;
    color: #854f41;
    font-family: 'Roboto Condensed', sans-serif;
}
.confira-medidas {
    display: inline-block;
    padding: .2rem 1rem;
    background: #000 !important;
    color: #fff;
    text-transform: uppercase;
    /* margin-left: 2rem; */
    font-size: 12px;
}
.product__buy:before {
    content: "Antes de comprar, confira nossa tabela de medidas";
    font-size: 12px;
    font-weight: 700;
} 
}

.product__name{
     font-size: 23px;
    font-style: italic; 
}
.prices--literal small {
    font-size: 100%;
    text-transform: initial;
}
.product__short-stock{
  display: none !important;
}
@media (min-width: 992px){
.footer__title[aria-expanded]:hover {
    cursor: auto;
    color: var(--dark);
    color: white;
    font-weight: 900;
  }}
.footer{
  text-transform: uppercase !important;
}
#main > footer > div > div.row > div.col-12.col-md-3.order-lg-last > div > ul > li:nth-child(3) > a{
  text-transform: lowercase !important;
}
#main > footer > div > div.row > div.col-12.col-md-3.order-lg-first > p{
  font-size: 12px;
    text-transform: none;
}
#main > footer > div > div.row > div.col-12.col-md-3.order-lg-last > div > ul > li:nth-child(2) > a{
  text-transform: lowercase;
}
.breadcrumb {
    padding: .5rem 2rem;
    margin-top: 0;
    background: #675562 !important;
  color: white !important;
}
.page-title__head {
    display: block;
    color: #854f41;
    margin-bottom: var(--spacer-2);
}
#cart-quickview > aside > footer > div > strong > div > span > div.prices__installments{
  font-size: 15px !important;
}
#cart-quickview > aside > footer > a.minicart__btn-checkout.btn.btn-block.btn-primary{
 background: #514240 !important; 
}
.shipping-calculator__services .active {
    cursor: auto;
    color: white !important;
}
#cart > div > span > div > div.col-md-5.col-lg-4.mt-3.mt-md-0 > div.cart__info > a.cart__btn-checkout.btn.btn-lg.btn-block.btn-success{
 text-transform: uppercase;
    background: #855042;
    border: none;
    border-radius: 0px; 
}
#cart > div > span > div > div.col-md-7.col-lg-8 > div.cart__back.d-none.d-md-block.my-4 > a{
  background: beige !important;
}
.app__contact{
  display: none !important;
}
#checkout > div > span > div > div > div.col-lg-8 > div > div:nth-child(2) > div > div.payment > span > div > div > div > button
{
  text-transform: uppercase !important;
}
#content > div > div:nth-child(1){

}
#main > div.ecom-credits > a:nth-child(2){
  display: none !important;
}
.ecom-credits a{
  text-align: center !important;
}
#eapps-instagram-feed-1 > a{
  display: none !important;
}
#product-actions > div > p.product__prices > div > span > div.prices__installments > small:nth-child(1){
display: none;
}
.product__buy button {
    width: 100%;
    text-transform: uppercase;
    background: #09865d;
    border-radius: 0;
    border: none;
    height: 61px;
    font-size: 26px;
}
#product-actions > div > div.product__buy > span > button > span > i{
  display: none !important;
}
#cart-quickview > aside > footer > a.minicart__btn-checkout.btn.btn-block.btn-primary {
    background: #09865d !important;
    text-transform: uppercase;
    font-weight: 800;
    border: none;
    border-radius: 0px !important;
    height: 63px;
    text-align: center;
    padding-top: 20px;
    font-size: 17px;
}
