css:
@import url('https://fonts.googleapis.com/css2?family=Roboto+Condensed&display=swap');

.body{
  font-family: 'Roboto Condensed', sans-serif !important;
}
#header{
 background: #fcf1f1 !important;
 padding-bottom: 10px !important;
}
.header{
  position: relative !important;
}
.prices__installments{
   font-size: 20px !important;
}
.prices__installments span{
  font-weight: 600;
}
#menu-horizontal{
  padding: 15px !important;
  font-family: 'Roboto Condensed', sans-serif;
}
.header__nav a{
  border-radius: 0px !important;
  font-size: 18px !important;

}
.header__nav a:hover {
    background: #854f41;
    text-decoration: none;
    border-radius: 0px !important;
    color: white;
    padding: 7px;
}

@media (min-width: 768px){
.header__search-input {

  border-radius: 0px !important;
  border: 1px solid #854f41;

  }}

#user-button > span.d-none.d-md-inline , .header__buttons .btn{
text-transform: uppercase;
    color: #854f41 !important;
    font-weight: 600;
}
#content > div > section > div > div > span{
  text-transform: uppercase;
  font-weight: 700;
  font-size: 26px !important;
  color: #854f41;
  font-family: 'Roboto Condensed', sans-serif;
}

.products-carousel__title a, .products-carousel__title span{
  font-size: 20px;
  text-transform: uppercase;
  color: #854f41;
}


@media (min-width: 992px){
.product-card__pictures {
    height: 400px !important;
}}
@media (min-width: 992px){
.product-card__picture img {
    max-height: 400px !important;
}}


.product-card__name{
  text-align: center !important;
    font-style: italic !important;
    font-size: 14px !important;
}
.product-card{
  text-align: center !important;
}

.prices__value{
  display: none !important;
}
.prices__discount{
  font-size: 14px !important;
}
.product-card__buy.fade button {
  display:none;
}
.prices__discount span{
  font-weight: 600 !important;
  color: green !important;
}
@media (max-width: 500px){
.product-card__name {
    --max-lines: 3;
    /* padding-top: var(--spacer-1); */
    font-size: var(--font-size-sm);
    line-height: var(--line-height-sm);
    /* height: calc(var(--line-height-sm)*var(--font-size-sm)*var(--max-lines)); */
    box-sizing: content-box!important;
    overflow: hidden;
    font-weight: var(--font-normal);
    z-index: 1;
    display: -webkit-box;
    -webkit-line-clamp: var(--max-lines);
    -webkit-box-orient: vertical;
    /* height: 0px; */
  }
.prices__installments {
    font-size: 15px !important;
}

.prices__discount {
    font-size: 12px !important;
}  
.products-carousel__title a, .products-carousel__title span {
    font-size: 15px;
    text-transform: uppercase;
    color: #854f41;
}
  .product-card__pictures {
    height: 250px;
}
.product-card__picture img {
    max-height: 250px;
}
  #content > div > section > div > div > span {
    text-transform: uppercase;
    font-weight: 700;
    font-size: 15px !important;
    color: #854f41;
    font-family: 'Roboto Condensed', sans-serif;
}
.confira-medidas {
    display: inline-block;
    padding: .2rem 1rem;
    background: #ee0642;
    color: #fff;
    text-transform: uppercase;
    /* margin-left: 2rem; */
    font-size: 12px;
}
.product__buy:before {
    content: "Antes de comprar, confira nossa tabela de medidas";
    font-size: 12px;
    font-weight: 700;
} 
#header{
  background: white !important;
}
}

.product__name{
     font-size: 23px;
    font-style: italic; 
}
.prices--literal small {
    font-size: 100%;
    text-transform: initial;
}
.product__short-stock{
  display: none !important;
}
@media (min-width: 992px)
.footer__title[aria-expanded]:hover {
    cursor: auto;
    color: var(--dark);
    color: white;
    font-weight: 900;
}
.footer{
  text-transform: uppercase !important;
}
#main > footer > div > div.row > div.col-12.col-md-3.order-lg-last > div > ul > li:nth-child(3) > a{
  text-transform: lowercase !important;
}
